class A{    public static void main(String[] args) {        int arr[]={7,8,3,1,2};        for(int i=0;i<arr.length-1;i++){            int small=i;            for(int j=1+i;j<arr.length;j++){                if(arr[small]>arr[j]){                    small=j;                }            }            int temp =arr[i];            arr[i]=arr[small];                        arr[small]=temp;        }      for(int Rahul=0;Rahul<arr.length;Rahul++){            System.out.println(arr[Rahul]);        }    }}